// server.js

const express = require('express');
const nodemailer = require('nodemailer');
const bodyParser = require('body-parser');
const app = express();

app.use(bodyParser.json());

// Nodemailer configuration
const transporter = nodemailer.createTransport({
    service: 'gmail', // You can use another email provider if you prefer
    auth: {
        user: 'ademolafrancis45@gmail.com',       // Replace with your email
        pass: 'Junero234'         // Replace with your email password
    }
});

// Endpoint to handle the form submission
app.post('/contact', (req, res) => {
    const { name, email, message } = req.body;

    // Email content
    const mailOptions = {
        from: 'your-email@gmail.com',
        to: 'your-email@gmail.com', // Replace with your email to receive the message
        subject: 'New Contact Form Submission',
        text: `Name: ${name}\nEmail: ${email}\nMessage: ${message}`
    };

    // Send the email
    transporter.sendMail(mailOptions, (error, info) => {
        if (error) {
            console.error(error);
            return res.status(500).send('Error sending message');
        }
        res.send('Message sent successfully');
    });
});

// Start the server
app.listen(3000, () => console.log('Server running on port 3000'));
